import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import '../styles/ClinicalSettings.css';

function ClinicalSettings() {
  const [formData, setFormData] = useState({
    clinicName: '',
    address: '',
    phoneNumber: '',
    operatingHours: '',
  });

  const [passwordData, setPasswordData] = useState({
    oldPassword: '',
    newPassword: '',
  });

  const [message, setMessage] = useState('');
  const [passwordMessage, setPasswordMessage] = useState('');
  const [error, setError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchClinicData = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/'); // Redirect to login if not authenticated
        return;
      }

      try {
        const response = await fetch('http://localhost:8070/api/clinics/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.ok) {
          const data = await response.json();
          setFormData({
            clinicName: data.clinicName || '',
            address: data.address || '',
            phoneNumber: data.phoneNumber || '',
            operatingHours: data.operatingHours || '',
          });
        } else {
          throw new Error('Failed to fetch clinic data');
        }
      } catch (error) {
        setError('Failed to load clinic information');
        console.error(error);
      }
    };

    fetchClinicData();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:8070/api/clinics/update-profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.msg);
        setShowModal(true); // Show modal on success
      } else {
        throw new Error('Failed to update clinic information');
      }
    } catch (error) {
      setError('Clinic update failed');
      console.error(error);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:8070/api/clinics/change-password', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(passwordData),
      });

      if (response.ok) {
        const data = await response.json();
        setPasswordMessage(data.message);
        setPasswordData({ oldPassword: '', newPassword: '' });
      } else {
        const errorData = await response.json();
        setPasswordError(errorData.message || 'Failed to change password');
      }
    } catch (error) {
      setPasswordError('An error occurred while changing the password');
      console.error(error);
    }
  };

  const closeModal = () => {
    setShowModal(false); // Close the modal
  };

  return (
    <div className="clinical-settings-page-container">
      <Sidebar />
      <div className="clinical-settings-content">
        <h2>Clinic Settings</h2>
        <p>Update your clinic's information below</p>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Clinic Name</label>
            <input
              type="text"
              name="clinicName"
              value={formData.clinicName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Address</label>
            <input
              type="text"
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Operating Hours</label>
            <input
              type="text"
              name="operatingHours"
              value={formData.operatingHours}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="save-button">
            Save Changes
          </button>
        </form>

        <hr />

        <h3>Change Password</h3>
        {passwordError && <p className="error-message">{passwordError}</p>}
        {passwordMessage && <p className="success-message">{passwordMessage}</p>}
        <form onSubmit={handlePasswordSubmit}>
          <div className="form-group">
            <label>Old Password</label>
            <input
              type="password"
              name="oldPassword"
              value={passwordData.oldPassword}
              onChange={handlePasswordChange}
              required
            />
          </div>
          <div className="form-group">
            <label>New Password</label>
            <input
              type="password"
              name="newPassword"
              value={passwordData.newPassword}
              onChange={handlePasswordChange}
              required
            />
          </div>
          <button type="submit" className="save-button">
            Change Password
          </button>
        </form>

        {showModal && (
          <div className="modal-overlay">
            <div className="modal">
              <h3>Clinic information updated successfully!</h3>
              <p>{message}</p>
              <button onClick={closeModal} className="close-modal-button">
                OK
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default ClinicalSettings;
