import React, { useState } from "react";
import axios from "axios";
import SignupDoctor from "./SignupDoctor";
import SignupClinical from "./SignupClinical";
import SignupAdmin from "./SignupAdmin";
import "../styles/signup.css";

function Signup() {
  const [selectedRole, setSelectedRole] = useState("");

  const handleRoleChange = (event) => {
    setSelectedRole(event.target.value);
  };

  const handleSignup = async (userData) => {
    try {
      const response = await axios.post("http://localhost:8070/api/auth/register", userData);
      if (response.data.success) {
        alert("Signup successful!");
      } else {
        alert("Signup failed: " + response.data.message);
      }
    } catch (error) {
      alert("Error signing up");
    }
  };

  return (
    <div className="signup-container">
      <h2>Signup Page</h2>
      <div className="role-selection">
        <label>
          <input
            type="radio"
            value="doctor"
            checked={selectedRole === "doctor"}
            onChange={handleRoleChange}
          />
          Doctor
        </label>
        <label>
          <input
            type="radio"
            value="clinical"
            checked={selectedRole === "clinical"}
            onChange={handleRoleChange}
          />
          Clinical Staff
        </label>
        <label>
          <input
            type="radio"
            value="admin"
            checked={selectedRole === "admin"}
            onChange={handleRoleChange}
          />
          Admin
        </label>
      </div>

      {selectedRole === "doctor" && <SignupDoctor onSignup={handleSignup} />}
      {selectedRole === "clinical" && <SignupClinical onSignup={handleSignup} />}
      {selectedRole === "admin" && <SignupAdmin onSignup={handleSignup} />}
    </div>
  );
}

export default Signup;
