import React from 'react';
import '../styles/ClinicalSetting.css'; // Ensure this CSS file matches the style of the first image

function ClinicalSetting() {
  const [currentInfo, setCurrentInfo] = useState({
    phoneNumber: 'Loading...',
    email: 'Loading...',
    name: 'Loading...',
  });

  useEffect(() => {
    // Simulate API call to fetch current information
    fetch('/api/clinical-staff-info') // Replace with your actual API endpoint
      .then((response) => response.json())
      .then((data) => {
        setCurrentInfo(data);
      })
      .catch((error) => {
        console.error('Error fetching current information:', error);
      });
  }, []);

  return (
    <div className="settings-container">
      <div className="sidebar">
        <ul>
          <li>Bill</li>
          <li>Report</li>
          <li className="active">Settings</li>
        </ul>
      </div>

      <div className="settings-content">
        <h2>Settings</h2>

        <div className="form-section">
          <h3>Current Information</h3>
          <p><strong>Name:</strong> {currentInfo.name}</p>
          <p><strong>Email:</strong> {currentInfo.email}</p>
          <p><strong>Phone Number:</strong> {currentInfo.phoneNumber}</p>
        </div>

        <div className="form-section">
          <h3>Change Profile Picture</h3>
          <form>
            <input type="file" />
            <button className="btn-primary" type="button">Update Profile Picture</button>
          </form>
        </div>

        <div className="form-section">
          <h3>Reset Account Password</h3>
          <form>
            <input type="password" placeholder="Current Password" />
            <input type="password" placeholder="New Password" />
            <input type="password" placeholder="Confirm New Password" />
            <button className="btn-primary" type="button">Reset Password</button>
          </form>
        </div>

        <div className="form-section">
          <h3>Change Phone Number</h3>
          <form>
            <input type="text" placeholder="New Phone Number" />
            <button className="btn-primary" type="button">Update Phone Number</button>
          </form>
        </div>

        <button className="btn-danger" type="button">Log Out</button>
      </div>
    </div>
  );
}

export default ClinicalSetting;
