import React, { useState } from "react";
import Sidebar from "./Sidebar"; // Import your Sidebar component
import "../styles/Settings.css";

function ClinicalSetting() {
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"

  const handlePasswordChange = (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setMessageType("error");
      setMessage("Passwords do not match.");
      return;
    }
    // Implement password reset logic here (e.g., API call)
    setMessageType("success");
    setMessage("Password reset successfully!");
  };

  const handlePhoneNumberChange = (e) => {
    e.preventDefault();
    // Implement phone number update logic here (e.g., API call)
    setMessageType("success");
    setMessage("Phone number updated successfully!");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  return (
    <div style={{ display: "flex" }}>
      {/* Sidebar */}
      <Sidebar />

      <div className="settings-container">
        <div className="settings-content">
          <h2>Settings</h2>
          <hr />

          {/* Change Profile Picture */}
          <div className="profile-section">
            <img
              src="/assets/doctorphoto.jpg" // Replace with your dynamic image path
              alt="Profile"
              className="profile-image"
            />
            <button className="btn">Change Profile Picture</button>
          </div>

          {/* Reset Password */}
          <div className="password-reset-section">
            <h3>Reset Account Password</h3>
            <form onSubmit={handlePasswordChange}>
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Confirm Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <button type="submit" className="btn">
                Reset Password
              </button>
            </form>
          </div>

          {/* Update Phone Number */}
          <div className="phone-update-section">
            <h3>Change Phone Number</h3>
            <form onSubmit={handlePhoneNumberChange}>
              <input
                type="text"
                placeholder="Phone Number"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                required
              />
              <button type="submit" className="btn">
                Update Phone Number
              </button>
            </form>
          </div>

          {/* Logout Button */}
          <button className="logout-btn" onClick={handleLogout}>
            Log Out
          </button>

          {/* Message */}
          {message && (
            <p className={`message ${messageType === "success" ? "success" : "error"}`}>
              {message}
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ClinicalSetting;
