import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import '../styles/Login.css';

function Login() {
  // State hooks for email, password, role, etc.
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("doctor");
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // handleLogin function to handle form submission
 const handleLogin = async (e) => {
    e.preventDefault();
    setError(""); // Clear previous errors
    try {
      const response = await axios.post("http://localhost:8070/api/auth/login", {
        email,
        password,
        role,
      });

      if (response.data.success) {
        navigate(`/${role}-home`); // Redirect based on role
      } else {
        setError("Invalid credentials");
      }
    } catch (error) {
      console.error("Login Error:", error); // Log the full error object
      setError("Error logging in. Please try again.");
    }
  };
 

  // JSX block (rendering the login form)
  return (
    <div className="login-container">
      <h2>Welcome Back!</h2>
      <h3>Login Page</h3>
      <form onSubmit={handleLogin}> {/* Form submission triggers handleLogin */}
        <div className="role-selection">
          <input
            type="radio"
            id="doctor"
            name="role"
            value="doctor"
            checked={role === "doctor"}
            onChange={() => setRole("doctor")}
          />
          <label htmlFor="doctor">Doctor</label>
          
          <input
            type="radio"
            id="clinical"
            name="role"
            value="clinical"
            checked={role === "clinical"}
            onChange={() => setRole("clinical")}
          />
          <label htmlFor="clinical">Clinical Staff</label>

          <input
            type="radio"
            id="admin"
            name="role"
            value="admin"
            checked={role === "admin"}
            onChange={() => setRole("admin")}
          />
          <label htmlFor="admin">Admin</label>
        </div>

        <input
          type="email"
          placeholder="Email or Username"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type={showPassword ? "text" : "password"}
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <div className="show-password">
          <input
            type="checkbox"
            checked={showPassword}
            onChange={() => setShowPassword(!showPassword)}
          />
          <label>Show Password</label>
        </div>

        <button type="submit">Login</button>
        {error && <p className="error">{error}</p>}
      </form>
      <p>
        Forgot Password? <a href="/forgot-password">Reset it</a>
      </p>
      <p>
        Don’t have an account? <a href="/signup">Register Now</a> 
      </p>
    </div>
  );
}

export default Login;
