// src/components/Login.js
import React, { useState } from 'react'; // Combined import statement
import '../styles/Login.css'; 
import axios from 'axios';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('doctor'); // Default role

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8070/api/auth/login', {
                username,
                password,
                role
            });
            alert(response.data.message);
            // Handle successful login, e.g., redirect to home page
        } catch (error) {
            alert(error.response.data.message); // Handle error
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
            />
            <select value={role} onChange={(e) => setRole(e.target.value)}>
                <option value="doctor">Doctor</option>
                <option value="staff">Clinical Staff</option>
                <option value="admin">Admin</option>
            </select>
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
