import React, { useState, useEffect } from "react";
import "../styles/ClinicalSetting.css";
import ClinicalSidebar from "./ClinicalSidebar.js";
import axios from 'axios';
function ClinicalSetting() {
  const [currentPhoneNumber, setCurrentPhoneNumber] = useState("");
  const [newPhoneNumber, setNewPhoneNumber] = useState("");
  const [currentPassword, setCurrentPassword] = useState("");
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [profilePicture, setProfilePicture] = useState(null);
  const [message, setMessage] = useState("");
  const [messageType, setMessageType] = useState(""); // "success" or "error"

  // Fetch user details when the component mounts
  useEffect(() => {
    async function fetchUserDetails() {
      try {
        const token = localStorage.getItem("token");
        const response = await fetch("/api/clinical-settings/clinical/details", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await response.json();
        if (response.ok) {
          setCurrentPhoneNumber(data.phoneNumber);
          setProfilePicture(data.profilePicture || "/assets/default-profile.png");
        } else {
          setMessageType("error");
          setMessage(data.message || "Failed to fetch user details.");
        }
      } catch (error) {
        setMessageType("error");
        setMessage("An error occurred while fetching user details.");
      }
    }
    fetchUserDetails();
  }, []);

  const handlePasswordChange = async (e) => {
    e.preventDefault();
  
    // Ensure both passwords match
    if (newPassword !== confirmPassword) {
      setMessageType("error");
      setMessage("Passwords do not match.");
      return;
    }
  
    try {
      const response = await fetch("/api/clinical-settings/clinical/update-password", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // Send JWT token for authentication
        },
        body: JSON.stringify({
          currentPassword: phoneNumber, // Replace 'phoneNumber' with the current password input variable
          newPassword: newPassword, // Send the new password to the backend
        }),
      });
  
      const data = await response.json();
  
      if (response.ok) {
        setMessageType("success");
        setMessage(data.message || "Password updated successfully.");
      } else {
        setMessageType("error");
        setMessage(data.message || "Failed to update password.");
      }
    } catch (error) {
      console.error("Error while updating password:", error);
      setMessageType("error");
      setMessage("An unexpected error occurred.");
    }
  };
  

  const updatePhoneNumber = async (phoneNumber) => {
    try {
      const response = await axios.put('http://localhost:3000/clinical-staff/update-phone-number', {
        userId: currentUser.id, // Replace with the current user's ID
        phoneNumber,
      });
      alert(response.data.message);
    } catch (error) {
      console.error('Error updating phone number:', error.response?.data || error.message);
      alert('An error occurred while updating the phone number.');
    }
  };

  const handleProfilePictureChange = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("profilePicture", profilePicture);

    try {
      const token = localStorage.getItem("token");
      const response = await fetch("/api/clinical-settings/clinical/update-profile-picture", {
        method: "PUT",
        headers: { Authorization: `Bearer ${token}` },
        body: formData,
      });
      const data = await response.json();
      if (response.ok) {
        setMessageType("success");
        setMessage("Profile picture updated successfully!");
        setProfilePicture(URL.createObjectURL(profilePicture));
      } else {
        setMessageType("error");
        setMessage(data.message || "Failed to update profile picture.");
      }
    } catch (error) {
      setMessageType("error");
      setMessage("An error occurred while updating the profile picture.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  return (
    <div style={{ display: "flex" }}>
      <ClinicalSidebar role="clinicalStaff" />

      <div className="settings-container">
        <div className="settings-content">
          <h2>Settings</h2>
          <hr />

          {/* Current Information */}
          <div className="current-info">
            <h3>Current Information</h3>
            <p><strong>Phone Number:</strong> {currentPhoneNumber || "Loading..."}</p>
          </div>

          {/* Change Profile Picture */}
          <div className="profile-section">
            <h3>Change Profile Picture</h3>
            <img
              src={profilePicture || "/assets/default-profile.png"}
              alt="Profile"
              className="profile-image"
            />
            <form onSubmit={handleProfilePictureChange}>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => setProfilePicture(e.target.files[0])}
                required
              />
              <button type="submit" className="btn">
                Update Profile Picture
              </button>
            </form>
          </div>

          {/* Reset Password */}
          <div className="password-reset-section">
            <h3>Reset Account Password</h3>
            <form onSubmit={handlePasswordChange}>
              <input
                type="password"
                placeholder="Current Password"
                value={currentPassword}
                onChange={(e) => setCurrentPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="New Password"
                value={newPassword}
                onChange={(e) => setNewPassword(e.target.value)}
                required
              />
              <input
                type="password"
                placeholder="Confirm New Password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
              <button type="submit" className="btn">
                Reset Password
              </button>
            </form>
          </div>

          {/* Update Phone Number */}
          <div className="phone-update-section">
            <h3>Change Phone Number</h3>
            <form onSubmit={handlePhoneNumberChange}>
              <input
                type="text"
                placeholder="New Phone Number"
                value={newPhoneNumber}
                onChange={(e) => setNewPhoneNumber(e.target.value)}
                required
              />
              <button type="submit" className="btn">
                Update Phone Number
              </button>
            </form>
          </div>

          {/* Logout Button */}
          <button className="logout-btn" onClick={handleLogout}>
            Log Out
          </button>

          {/* Message */}
          {message && (
            <p className={`message ${messageType === "success" ? "success" : "error"}`}>
              {message}
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default ClinicalSetting;
