// src/components/SignupDoctor.js
import '../styles/SignupDoctor.css';
import React, { useState } from 'react';
import axios from 'axios';
//import './SignupDoctor.css';

const SignupDoctor = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    medicalLicenseNumber: '',
    specialization: '',
    yearsOfExperience: '',
    phoneNumber: '',
    password: '',
    passwordConfirmation: '',
    role: 'Doctor'
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password !== formData.passwordConfirmation) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:8070/api/auth/register', formData);
      if (response.data.success) {
        setSuccess('Registration successful!');
      } else {
        setError('Registration failed');
      }
    } catch (err) {
      setError('Error occurred while registering');
    }
  };

  return (
    <div className="signup-container">
      <div className="signup-form">
        <h2>Signup Page</h2>
        <h3>Create Your Account</h3>
        {error && <p className="error">{error}</p>}
        {success && <p className="success">{success}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="fullName"
            placeholder="Full Name"
            value={formData.fullName}
            onChange={handleChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="medicalLicenseNumber"
            placeholder="Medical License Number"
            value={formData.medicalLicenseNumber}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="specialization"
            placeholder="Specialization"
            value={formData.specialization}
            onChange={handleChange}
            required
          />
          <input
            type="number"
            name="yearsOfExperience"
            placeholder="Years of Experience"
            value={formData.yearsOfExperience}
            onChange={handleChange}
            required
          />
          <input
            type="text"
            name="phoneNumber"
            placeholder="Phone Number"
            value={formData.phoneNumber}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="passwordConfirmation"
            placeholder="Password Confirmation"
            value={formData.passwordConfirmation}
            onChange={handleChange}
            required
          />
          <div className="terms">
            <input type="checkbox" required /> I agree to the terms and conditions.
          </div>
          <button type="submit">Register</button>
        </form>
      </div>
    </div>
  );
};

export default SignupDoctor;
