import "../styles/ClinicalHome.css";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import ClinicalSidebar from "./ClinicalSidebar.js"; // Import the ClinicalSidebar component

function ClinicalHome() {
  const navigate = useNavigate();

  // State for patient data
  const [patients, setPatients] = useState([]);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const [clinicalStaffName, setClinicalStaffName] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  const API_BASE_URL = process.env.REACT_APP_API_URL || "http://localhost:8070";

  useEffect(() => {
    const fetchPatients = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        navigate("/"); // Redirect to login if no token
        return;
      }

      try {
        // Decode the JWT token to get the clinical staff's name
        const tokenPayload = JSON.parse(atob(token.split(".")[1]));
        setClinicalStaffName(tokenPayload.name);

        // Fetch patients data
        const response = await fetch(`${API_BASE_URL}/api/clinicalstaff/patients`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          if (response.status === 401) {
            setError("Session expired. Please log in again.");
            localStorage.removeItem("token");
            navigate("/");
          } else {
            throw new Error("Failed to fetch patient data");
          }
        } else {
          const data = await response.json();
          setPatients(data);
        }
      } catch (error) {
        console.error("Error fetching patient data:", error);
        setError("Failed to load patient data. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchPatients();
  }, [navigate, API_BASE_URL]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/");
  };

  const today = new Date().toLocaleDateString("en-US", {
    weekday: "long",
    month: "long",
    day: "numeric",
  });

  const handlePatientClick = (patient) => {
    setSelectedPatient(patient);
  };

  return (
    <div className="clinical-home-container">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="logo">CarePlus</div>
        <ClinicalSidebar />
        <nav>
          <ul>
            <li>Bill</li>
            <li>Report</li>
            <li>Settings</li>
          </ul>
        </nav>
      </aside>

      {/* Main Content */}
      <main className="main-content">
        <header className="header">
          <div>Prescriptions</div>
          <div className="profile">{clinicalStaffName || "Clinical Staff"}</div>
        </header>

        {loading ? (
          <p>Loading...</p>
        ) : error ? (
          <p className="error">{error}</p>
        ) : (
          <div className="content">
            {/* Patient List */}
            <div className="patient-list">
              <h3>Totally {patients.length} Patients</h3>
              <ul>
                {patients.map((patient) => (
                  <li
                    key={patient._id}
                    onClick={() => handlePatientClick(patient)}
                    style={{
                      backgroundColor:
                        selectedPatient?._id === patient._id ? "#cceeff" : "#fff",
                      cursor: "pointer",
                    }}
                  >
                    {patient.name}
                  </li>
                ))}
              </ul>
              <button className="see-all">see all</button>
            </div>

            {/* Fee Details */}
            <div className="fee-details">
              <input type="text" placeholder="Search" />
              {selectedPatient ? (
                <div className="fee-info">
                  <h4>{selectedPatient.name}</h4>
                  <div>Doctor fee: <input type="text" /></div>
                  <div>Report fee: <input type="text" /></div>
                  <div>Clinic’s fee: <input type="text" /></div>
                  <div>Total fee: <input type="text" /></div>
                </div>
              ) : (
                <p>Select a patient to view details.</p>
              )}
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

export default ClinicalHome;
