import React, { useState } from "react";
import "../styles/Settings.css";

function Settings() {
  const [newPassword, setNewPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [message, setMessage] = useState("");

  const handlePasswordChange = (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setMessage("Passwords do not match.");
      return;
    }
    // Implement password reset logic here (e.g., API call)
    setMessage("Password reset successfully!");
  };

  const handlePhoneNumberChange = (e) => {
    e.preventDefault();
    // Implement phone number update logic here (e.g., API call)
    setMessage("Phone number updated successfully!");
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.href = "/";
  };

  return (
    <div className="settings-container">
      <div className="sidebar">
        <h2>CarePlus</h2>
        <ul>
          <li>Bill</li>
          <li>Report</li>
          <li className="active">Settings</li>
        </ul>
      </div>
      <div className="settings-content">
        <h2>Settings</h2>
        <hr />

        {/* Change Profile Picture */}
        <div className="profile-section">
          <img
            src="/path-to-profile-image.jpg" // Replace with the actual path to the profile picture
            alt="Profile"
            className="profile-image"
          />
          <button className="btn">Change Profile Picture</button>
        </div>

        {/* Reset Password */}
        <div className="password-reset-section">
          <h3>Reset account password</h3>
          <form onSubmit={handlePasswordChange}>
            <input
              type="password"
              placeholder="Password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Confirm Password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <button type="submit" className="btn">
              Reset Password
            </button>
          </form>
        </div>

        {/* Update Phone Number */}
        <div className="phone-update-section">
          <h3>Change Phone Number</h3>
          <form onSubmit={handlePhoneNumberChange}>
            <input
              type="text"
              placeholder="Phone Number"
              value={phoneNumber}
              onChange={(e) => setPhoneNumber(e.target.value)}
              required
            />
            <button type="submit" className="btn">
              Update Phone Number
            </button>
          </form>
        </div>

        {/* Logout Button */}
        <button className="logout-btn" onClick={handleLogout}>
          Log Out
        </button>

        {/* Message */}
        {message && <p className="message">{message}</p>}
      </div>
    </div>
  );
}

export default Settings;
