// src/components/SignupDoctor.js

import React, { useState } from 'react';
import axios from 'axios';
import '../styles/signupDoctor.css'; // Import your styles

function SignupDoctor() {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [medicalLicenseNumber, setMedicalLicenseNumber] = useState('');
  const [specialization, setSpecialization] = useState('');
  const [yearsOfExperience, setYearsOfExperience] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [password, setPassword] = useState('');
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    // Implement signup logic
    try {
      const response = await axios.post('http://localhost:8070/api/auth/register', {
        fullName,
        email,
        medicalLicenseNumber,
        specialization,
        yearsOfExperience,
        phoneNumber,
        password,
        role: 'Doctor', // Set role to Doctor
      });
      alert(response.data.message); // Handle success
    } catch (error) {
      alert(error.response.data.message); // Handle error
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" placeholder="Full Name" onChange={(e) => setFullName(e.target.value)} required />
      <input type="email" placeholder="Email" onChange={(e) => setEmail(e.target.value)} required />
      <input type="text" placeholder="Medical License Number" onChange={(e) => setMedicalLicenseNumber(e.target.value)} required />
      <input type="text" placeholder="Specialization" onChange={(e) => setSpecialization(e.target.value)} required />
      <input type="number" placeholder="Years of Experience" onChange={(e) => setYearsOfExperience(e.target.value)} required />
      <input type="text" placeholder="Phone Number" onChange={(e) => setPhoneNumber(e.target.value)} required />
      <input type="password" placeholder="Password" onChange={(e) => setPassword(e.target.value)} required />
      <button type="submit">Register as Doctor</button>
    </form>
  );
}

export default SignupDoctor;
