import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/ClinicalSetting.css'; // Ensure this CSS file matches the style of the first image
import Sidebar from './Sidebar';

function ClinicalSetting() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
  });

  const [passwordData, setPasswordData] = useState({
    oldPassword: '',
    newPassword: '',
  });

  const [message, setMessage] = useState('');
  const [passwordMessage, setPasswordMessage] = useState('');
  const [error, setError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [showModal, setShowModal] = useState(false); // State for modal visibility
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/'); // Redirect to login if not authenticated
        return;
      }

      try {
        const response = await fetch('http://localhost:8070/api/clinicalstaff/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (response.ok) {
          const data = await response.json();
          setFormData({
            fullName: data.fullName || '',
            email: data.email || '',
            phoneNumber: data.phoneNumber || '',
          });
        } else {
          throw new Error('Failed to fetch profile');
        }
      } catch (error) {
        setError('Failed to load profile');
        console.error(error);
      }
    };

    fetchProfile();
  }, [navigate]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handlePasswordChange = (e) => {
    const { name, value } = e.target;
    setPasswordData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:8070/api/clinicalstaff/update-profile', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        const data = await response.json();
        setMessage(data.msg);
        setShowModal(true); // Show modal on success
      } else {
        throw new Error('Failed to update profile');
      }
    } catch (error) {
      setError('Profile update failed');
      console.error(error);
    }
  };

  const handlePasswordSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/');
      return;
    }

    try {
      const response = await fetch('http://localhost:8070/api/clinicalstaff/change-password', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(passwordData),
      });

      if (response.ok) {
        const data = await response.json();
        setPasswordMessage(data.message);
        setPasswordData({ oldPassword: '', newPassword: '' });
      } else {
        const errorData = await response.json();
        setPasswordError(errorData.message || 'Failed to change password');
      }
    } catch (error) {
      setPasswordError('An error occurred while changing the password');
      console.error(error);
    }
  };

  const closeModal = () => {
    setShowModal(false); // Close the modal
  };

  return (
    <div className="settings-page-container">
      <Sidebar />
      <div className="settings-content">
        <h2>Settings</h2>
        <p>Update your profile information below</p>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Full Name</label>
            <input
              type="text"
              name="fullName"
              value={formData.fullName}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone Number</label>
            <input
              type="text"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleChange}
              required
            />
          </div>
          <button type="submit" className="save-button">
            Save Changes
          </button>
        </form>

        <hr />

<h3>Change Password</h3>
{passwordError && <p className="error-message">{passwordError}</p>}
{passwordMessage && <p className="success-message">{passwordMessage}</p>}
<form onSubmit={handlePasswordSubmit}>
  <div className="form-group">
    <label>Old Password</label>
    <input
      type="password"
      name="oldPassword"
      value={passwordData.oldPassword}
      onChange={handlePasswordChange}
      required
    />
  </div>
  <div className="form-group">
    <label>New Password</label>
    <input
      type="password"
      name="newPassword"
      value={passwordData.newPassword}
      onChange={handlePasswordChange}
      required
    />
  </div>
  <button type="submit" className="save-button">
    Change Password
  </button>
</form>

{/* Modal for Success Message */}
{showModal && (
  <div className="modal-overlay">
    <div className="modal">
      <h3>Profile updated successfully!</h3>
      <p>{message}</p>
      <button onClick={closeModal} className="close-modal-button">
        OK
      </button>
    </div>
  </div>
)}
</div>
</div>
);
}





export default ClinicalSetting;
